def get_tuple_of_unique_orders(LUS):
    LUS_list = LUS.split()
    LUS_set = set(LUS_list)
    LUS_list_unique = list(LUS_set)
    LUS_tuple_unique_without_WAN = tuple(item for item in LUS_list_unique if "WAN" not in item)
    return LUS_tuple_unique_without_WAN


def create_sorted_list_of_lists_containing_lu_by_orders():
    tuple_of_orders = get_tuple_of_unique_orders(LUS)
    # print(tuple_of_orders)
    lus_list = [[] for _ in range(len(tuple_of_orders))]
    checking_lu = ""
    for item in LUS.split():
        if "WAN" in item: #check if item is LU or order number if LU remember that specific LU
            checking_lu = item
        if "WAN" not in item: #check if item is LU or order number if order check ID and add LU to tle list of the list
            index_of_item = tuple_of_orders.index(item)
            lus_list[index_of_item].append(checking_lu)
    return sorted(lus_list, key=len)


